import { useState } from "react";
import useBInput from "../hooks/useb-input";

const BasicForm = (props) => {
  const {
    enteredInput: firstName,
    isTouched: firstNameTouched,
    enteredInputHasError: firstNameHasError,
  } = useBInput((value) => value.trim() !== "");

  const [enteredInput, setEnteredInput] = useState("");
  const [isTouched, setIsTouched] = useState(false);
  const enteredInputHasError = enteredInput.trim() === "" && isTouched;

  const inputValueHandler = (event) => {
    setEnteredInput(event.target.value);
  };

  const onBlurHandler = (event) => {
    setIsTouched(true);
    event.preventDefault();
  };

  let formIsValid = false;

  if (enteredInput) {
    formIsValid = true;
  }

  const formSubmitHandler = () => {};

  return (
    <form onSubmit={formSubmitHandler}>
      <div className="control-group">
        <div className="form-control">
          <label htmlFor="name">First Name</label>
          <input
            type="text"
            id="name"
            onChange={inputValueHandler}
            value={firstName}
            onBlur={onBlurHandler}
          />
          {enteredInputHasError && <p>Field must not be empty</p>}
        </div>
        <div className="form-control">
          <label htmlFor="name">Last Name</label>
          <input type="text" id="name" />
        </div>
      </div>
      <div className="form-control">
        <label htmlFor="name">E-Mail Address</label>
        <input type="text" id="name" />
      </div>
      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
