import { useEffect, useState } from "react";

const SimpleInput = (props) => {
  const [enteredName, setEnteredName] = useState("");
  const [enteredEmail, setEnteredEmail] = useState("");
  const [isNameTouched, setIsNameTouched] = useState(false);
  const [isEmailTouched, setIsEmailTouched] = useState(false);

  const isValidName = enteredName.trim() !== "";
  const isValidEmail =
    (enteredEmail.trim() !== "") & enteredEmail.includes("@");

  const nameInputIsInvalid = !isValidName && isNameTouched;
  const emailInputIsInvalid = !isValidEmail && isEmailTouched;

  let formIsValid = false;

  if (isValidName & isValidEmail) {
    formIsValid = true;
  }

  const nameInputHandler = (event) => {
    setEnteredName(event.target.value);
  };

  const emailInputHandler = (event) => {
    setEnteredEmail(event.target.value);
  };

  const nameInputBlurHandler = (event) => {
    event.preventDefault();
    setIsNameTouched(true);
  };

  const emailInputBlurHandler = (event) => {
    event.preventDefault();
    setIsEmailTouched(true);
  };

  const formSubmissionHandler = (event) => {
    event.preventDefault();
    setIsNameTouched(true);
    setIsEmailTouched(true);
    if (
      (enteredName.trim() === "") &
      (enteredEmail.trim() === "") &
      !enteredEmail.includes("@")
    ) {
      return;
    }

    console.log(enteredName, enteredEmail);
    setEnteredName("");
    setEnteredEmail("");
    setIsNameTouched(false);
    setIsEmailTouched(false);
  };

  const nameInputClass = nameInputIsInvalid
    ? "form-control invalid"
    : "form-control";

  const emailInputClass = emailInputIsInvalid
    ? "form-control invalid"
    : "form-control";

  const emailErrorText =
    (enteredEmail.trim() === "" && "Email must not be empty!!") ||
    (!enteredEmail.includes("@") && "Email must containe @");

  return (
    <form onSubmit={formSubmissionHandler}>
      <div className={nameInputClass}>
        <label htmlFor="name">Your Name</label>
        <input
          type="text"
          id="name"
          onChange={nameInputHandler}
          onBlur={nameInputBlurHandler}
          value={enteredName}
        />
      </div>
      {nameInputIsInvalid && (
        <p className="error-text">Name must not be empty!!</p>
      )}
      <div className={emailInputClass}>
        <label htmlFor="email">Your Email</label>
        <input
          type="email"
          id="name"
          onChange={emailInputHandler}
          onBlur={emailInputBlurHandler}
          value={enteredEmail}
        />
      </div>
      {emailInputClass && <p className="error-text">{emailErrorText}</p>}
      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
