import { useRef, useState } from "react";

const SimpleInput = (props) => {
  const nameInputRef = useRef();
  const [enteredName, setEnteredName] = useState("");
  const [isValidName, setIsValidName] = useState(false);
  const [isNameTouched, setIsNameTouched] = useState(false);

  const nameInputHandler = (event) => {
    setEnteredName(event.target.value);
  };

  const formSubmissionHandler = (event) => {
    event.preventDefault();
    if (enteredName.trim() === "") {
      setIsValidName(false);
      return;
    }
    setIsValidName(true);
    console.log(enteredName);
    const enteredVal = nameInputRef.current.value;
    console.log(enteredVal);
    setEnteredName("");
  };

  const nameInputIsValid = !isValidName && isNameTouched;

  const nameInputClass = nameInputIsValid
    ? "form-control"
    : "form-control invalid";

  return (
    <form onSubmit={formSubmissionHandler}>
      <div className={nameInputClass}>
        <label htmlFor="name">Your Name</label>
        <input
          ref={nameInputRef}
          type="text"
          id="name"
          onChange={nameInputHandler}
          value={enteredName}
        />
      </div>
      {!isValidName && <p className="error-text">Name must not be empty!!</p>}
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
